module.exports = {
    HelloWorld: function () {
        console.log('test function primes.HelloWorld() called.');
        return 0;
    },
    
    sieve: function(n) {
    // Computes first n primes.  For n=60,000,000, >3.5 million primes 
    // will be found in ~15 seconds.
    // Returns array of length n where nth entry = 1 if n is prime
    // and n = 0 if n is composite.

        // Initialize sieve array
        let s = new Array(n);
        for (var i=0; i<=n; i++) {
            s[i]=1;
        }
        
        // Mark all composite numbers
        for (var i=2; i<=n; i++) {
            for (var j=2; i*j<=n; j++) {
                s[i*j]=0;
            }
        }

    return s;

    } 
}


